function removeWalkSegments(segments, callback) {
    var updatedSegments = [];
    for (var i = 0, max = 10; i < segments.length; i++) {
        if (trips.vehicles[segments[i].vehicle].kind != 'foot') {
            updatedSegments.push(segments[i]);
        }
    }
    console.log(updatedSegments);
    callback(updatedSegments);
}

function findMinMax(array) {
    var indicativePrices;
    if (array) {
        if (array.length == 3) {
            indicativePrices = {
                priceLow: array[2].price,
                priceHigh: array[0].price
            };
        } else {
            if (array[0].highPrice)
                indicativePrices = array[0];
            else
                indicativePrices = {
                    priceLow: array[0].price,
                    priceHigh: array[0].price * 2
                };
        }
    } else {
        indicativePrices = {
            priceLow: 0,
            priceHigh: 0
        };
    }
    return indicativePrices;
}

function getExpandHeader(segment) {

    var expandHeader = $('#sharedPref #expand_header').clone();
    var indicativePrices = findMinMax(segment.indicativePrices);

    var dep = trips.places[segment.depPlace].shortName;
    dep = dep.toLowerCase().indexOf(trips.places[segment.depPlace].kind) != -1 ? dep :
        dep + capitalizeFirstLetter(trips.places[segment.depPlace].kind);
    $(expandHeader).find('span.from').text(dep);

    var to = trips.places[segment.arrPlace].shortName;
    to = to.toLowerCase().indexOf(trips.places[segment.arrPlace].kind) != -1 ? to :
        to + capitalizeFirstLetter(trips.places[segment.arrPlace].kind);
    $(expandHeader).find('span.to').text(to);

    $(expandHeader).find('.durations').text(
        getHrsMin(segment.transitDuration + segment.transferDuration) + "(" +
        "₹" + indicativePrices.priceLow + " - ₹" +
        indicativePrices.priceHigh + ")");
    $(expandHeader).find('.type_img').attr("src",
        images_start[trips.vehicles[segment.vehicle].kind]);
    $(expandHeader).find('.type_book').text(
        "Book " + trips.vehicles[segment.vehicle].name);
    return $(expandHeader).prop('outerHTML');
}

function capitalizeFirstLetter(string) {
    return " " + string.charAt(0).toUpperCase() + string.slice(1);
}

